import React, { Component } from 'react'

export default class Login extends Component {
    state = {
        email: '',
        password: '',
        data: null,
        isError: false
    }
    handleEmail = (event) => {
        this.setState({ email: event.target.value });
    }
    handlePassword = (event) => {
        this.setState({ password: event.target.value });
    }
    sendData = () => {
        // try {
        //     fetch('http://localhost:8080/login', {
        //         method: 'POST',
        //         headers: {
        //             'Content-Type': 'application/json'
        //         },
        //         body: JSON.stringify({
        //             user: {
        //                 email: this.state.email,
        //                 password: this.state.password
        //             }
        //         })
        //     })
        //         .then(responce => responce.json())
        //         .then(data => localStorage.setItem('token', data.token))
        // } catch (error) {
        //     this.setState({isError: true})
        //     console.log('huihui');
        // }

        fetch('http://localhost:8080/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                user: {
                    email: this.state.email,
                    password: this.state.password
                }
            })
        })
            .then(responce => responce.json())
            .then(data => {
                localStorage.setItem('token', data.token);
                return data.json();
            })
            .then(jsonData => this.props.history.push('/'))
            .catch(error => this.setState({ isError: true }))
    }
    render() {
        return (
            <div className="login">
                <div className="login__container">
                    <h2 className="login__title">Login</h2>
                    <span className="login__label">Your email</span>
                    <input onChange={this.handleEmail} type="text" className="login__input" />
                    <span className="login__label">Password</span>
                    <input onChange={this.handlePassword} type="text" className="login__input" />
                    <button onClick={this.sendData} className="login__button m_button">Continue</button>
                </div>
                {
                    this.state.isError && <h2>error</h2>
                }
            </div>
        )
    }
}
