import React, { Component } from 'react'

export default class Admin extends Component {
    state = {
        isAdmin: false
    }
    componentDidMount() {
        let user_data = localStorage.getItem('user_data');
        console.log(user_data.isAdmin);
        
        if (user_data.isAdmin === 1) { 
            this.setState({isAdmin: true});
        }        
    }
    render() {
        return (
            <div>
                <h1>hui</h1>
            </div>
        )
    }
}
