import React, { Component } from 'react'
import { Link } from 'react-router-dom'
export default class Cart extends Component {
    state = {
        products: null,
        cart: null,
        totalPrice: null
    }
    componentDidMount() {
        let cart = JSON.parse(localStorage.getItem('cart'));
        this.setState({ cart: cart });

        let total = 0;
        if (this.state.cart !== null) {
            cart.forEach(product => {
                total += product.price;
            });
        }
        this.setState({ totalPrice: total });
        fetch('http://localhost:8080/product')
            .then(responce => responce.json())
            .then(data => this.setState({ products: data }))
    }
    render() {
        console.log(this.state.cart);
        console.log(this.state.totalPrice);

        return (
            <div className="cart">
                {
                    this.state.cart && (
                        this.state.cart.map(product => {
                            return (
                                <div key={product.id} className="cart__product">
                                    <img src={`/images/${product.path}`} className="cart__img" alt={product.name} />
                                    <Link className="cart__product-name" to={`/product/${product.id}`}>{product.name}</Link>
                                    <span className="cart__product-price">{product.price} &#8372;</span>
                                </div>
                            )
                        })
                    )
                }
                {
                    this.state.totalPrice  ? (<h2 className="cart__total">Total: {this.state.totalPrice}</h2>) : (
                        <h2 className="cart__total">Total: {this.state.totalPrice}</h2>)
                    
                    
                }
            </div>
        )
    }
}
