import React, { Component } from 'react'

export default class Registration extends Component {
    state = {
        name: '',
        surname: '',
        email: '',
        phone_number: '',
        password: '',
    }
    handleName = (event) => {
        this.setState({name: event.target.value});
    }
    handleSurname = (event) => {
        this.setState({surname: event.target.value});
    }
    handleEmail = (event) => {
        this.setState({email: event.target.value});
    }
    handlePhoneNumber = (event) => {
        this.setState({phone_number: event.target.value});
    }
    handlePassword = (event) => {
        this.setState({password: event.target.value});
    }
    render() {
        console.log(this.state);
        
        return (
            <div className="registration">
                <h2 className="registration__title">Create account</h2>
                <span className="registration-label">Your name</span>
                <input onChange={this.handleName} className="registration-input" type="text" required/>
                <span className="registration-label">Surname</span>
                <input onChange={this.handleSurname} className="registration-input" type="text" required/>
                <span className="registration-label">Email</span>
                <input onChange={this.handleEmail} className="registration-input" type="text" required/>
                <span className="registration-label">Phone number</span>
                <input onChange={this.handlePhoneNumber} className="registration-input" type="text" required/>
                <span className="registration-label">Password</span>
                <input onChange={this.handlePassword} className="registration-input" type="password" required/>
                <button className="registration__button">Create your Sho(p) account</button>
            </div>
        )
    }
}
